import React, { useState, useEffect, useContext } from "react";
import { Drawer, Menu } from "antd";
import { useLocation, NavLink } from "react-router-dom";
import {
  DatabaseOutlined,
  ContainerOutlined,
  ArrowLeftOutlined,
  DesktopOutlined,
  TeamOutlined,
  LogoutOutlined,
  CopyOutlined,
  ProfileOutlined,
} from "@ant-design/icons";
import { useHistory } from "react-router-dom";
import { Link } from "react-router-dom";
import { Twirl as Hamburger } from "hamburger-react";
import Context from "context/context";
import { logout } from "context/actions";

import "./NavBar.scss";

const { SubMenu } = Menu;

export const Secretary = () => {
  const { dispatch } = useContext(Context);
  const [open, setOpen] = useState(false);
  const { pathname } = useLocation();
  let history = useHistory();

  useEffect(() => {
    if (pathname === "/secretary/selected") {
      setOpen(true);
    }
  }, []);

  return (
    <div className="NavBar">
      <Link to={"/secretary"} className="NavBar__logo">
        <img src={process.env.PUBLIC_URL + "/logo.png"} alt="" />
        <span>MUGRC</span>
      </Link>
      <div className="NavBar__hamburger">
        <Hamburger color={"#fff"} rounded size={22} toggled={open} toggle={setOpen} />
      </div>
      <Drawer placement="right" closable={true} onClose={() => setOpen(false)} visible={open}>
        <div className="Layout-logo">
          <img src={process.env.PUBLIC_URL + "/logo.png"} alt="" />
        </div>
        <Menu selectedKeys={[pathname]} defaultOpenKeys={open && ["sub1"]} mode="inline" style={{ marginTop: "1rem", border: "none" }}>
